import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";
import Button from "react-bootstrap/Button";
import { Link } from "react-router-dom";

const PreBill = (props) => {
  const [cusname, setCusName] = useState("");
  const [totalPayable, setTotalPayable] = useState();
  const [sub, setSub] = useState(0);
  const [ttl, setTtl] = useState(0);
  const [rund, setRund] = useState(0);
  const [gt, setGt] = useState(0);
  const [discountObj, setDiscountObj] = useState({});

  const [getAllLocalData, setGetAllLocalData] = useState(
    JSON.parse(localStorage.getItem("dataList")).map((e) => e)
  );
  const [dataFromStorage, setDataFromStorage] = useState(
    JSON.parse(localStorage.getItem("dataList")).map((e) => e)
  );

  const getDataFromLocalStorage = () => {
    let namekey = JSON.parse(localStorage.getItem("dataList"));
    if (namekey.length > 0) {
      let data = namekey.filter((x) => x.name === cusname);
      setDataFromStorage(data);
    } else {
      alert("No Data Found");
    }
  };

  const onCalculatedTotal = () => {
    let subTotal = 0;
    let total = 0;
    getAllLocalData.forEach((ele, i) => {
      subTotal = subTotal + ele.subtotal;
      total = total + ele.total;
    });
    setSub(subTotal);
    setTtl(total);
    setTotalPayable(Number(total) - Number(discountObj?.disValue));
    setRund((Math.round(total) - total).toFixed(2));
    setGt((total - subTotal).toFixed(2));
  };

  const setFormData = (value, name, arrInd) => {
    getAllLocalData[arrInd][name] = value;
    setGetAllLocalData([...getAllLocalData]);
    if (name === "price" || name === "quantity") {
      getAllLocalData[arrInd]["subtotal"] =
        getAllLocalData[arrInd]["price"] * getAllLocalData[arrInd]["quantity"];
      setGetAllLocalData([...getAllLocalData]);
      onCalculatedTotal();
    }
    if (name === "price" || name === "quantity" || name === "gst") {
      getAllLocalData[arrInd]["total"] =
        getAllLocalData[arrInd]["price"] * getAllLocalData[arrInd]["quantity"] +
        (getAllLocalData[arrInd]["price"] *
          getAllLocalData[arrInd]["quantity"] *
          getAllLocalData[arrInd]["gst"]) /
        100;
      setGetAllLocalData([...getAllLocalData]);
      onCalculatedTotal();
    }
  };

  const setDiscountData = (name, value) => {
    if (name === "disValue") {
      if (discountObj?.disType === "â‚¹") {
        setTotalPayable((ttl - parseInt(value).toFixed(2)));
      } else if (discountObj?.disType === "%") {
        let x = (ttl * parseInt(value)) / 100;
        setTotalPayable((ttl - x.toFixed(2)));
      }
    }
    discountObj[name] = value;
    setDiscountObj({ ...discountObj });
  };

  const deleteBill = (it) => {
    let tempData = [...getAllLocalData];
    tempData.splice(it, 1);
    setGetAllLocalData([...tempData])
    localStorage.setItem("dataList", JSON.stringify(tempData));
    let tempData2 = [...dataFromStorage];
    tempData2.splice(it, 1);
    setDataFromStorage([...tempData]);
  };

  const editAction = (singleObj) => {
    // let findInd = getAllLocalData.findIndex(x => x.id === singleObj.id);
    // let localData = [...getAllLocalData];
    // if(findInd !== -1){
    //   localData.splice(findInd, 1, singleObj)
    // }
  }

  return (
    <>
      <style>
        {`
      table {
  font-family: arial, sans-serif;
  border-collapse: collapse;
  width: 100%;
}

td, th {
  border: 1px solid #dddddd;
  text-align: left;
  padding: 8px;
}
      `}
      </style>
      <Link to="/add">
        <Button
          className="btn btn-info center m-lg-5"
          onClick={props.AddNewBill}
        >
          Add New Bill
        </Button>
      </Link>
      <InputGroup className="mb-3">
        {/* {console.log(dataFromStorage)} */}
        <InputGroup.Text>Name:</InputGroup.Text>
        <Form.Control
          aria-label="Name:"
          placeholder="Client's Name"
          value={cusname}
          onChange={(e) => setCusName(e.target.value)}
          required
        />
        <button
          type="button"
          disabled={!cusname}
          className="btn btn-success"
          onClick={getDataFromLocalStorage}
        >
          Search
        </button>
      </InputGroup>
      <table>
        <thead>
          <tr>
            <th>Bill Number</th>
            <th>Bill Name</th>
            <th>Bill Date</th>
            <th>Item</th>
            <th>Quantity</th>
            <th>Price</th>
            <th>GST</th>
            <th>Sub-total</th>
            <th>Total</th>
          </tr>
        </thead>
        <tbody>
          {dataFromStorage.map((data, i) => (
            <tr key={i}>
              <td>{data.number}</td>
              <td>{data.name}</td>
              <td>{data.date}</td>
              <td>{data.item}</td>
              <td>{data.quantity}</td>
              <td>{data.price}</td>
              <td>{data.gst}</td>
              <td>{data.subtotal}</td>
              <td>{data.total}</td>
              <Button
                className="btn btn-danger"
                onClick={() => {
                  deleteBill(i);
                }}
              >
                Delete
              </Button>
              <Button
                className="btn btn-danger"
                onClick={() => {
                  editAction(data);
                }}
              >
                Edit
              </Button>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

export default PreBill;
