import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";
import Button from "react-bootstrap/Button";
import { Link } from "react-router-dom";

const PreBill = (props) => {
  const [cusname, setCusName] = useState("");
  const [dataFromStorage, setDataFromStorage] = useState(JSON.parse(localStorage.getItem("dataList")).map((e)=>(e)));

  const getDataFromLocalStorage = () => {
    let namekey = JSON.parse(localStorage.getItem("dataList"));
    if (namekey.length > 0) {
      let data = namekey.filter((x) => x.name === cusname);
      setDataFromStorage(data);
    } else {
      alert("No Data Found");
    }
  };
  const deleteBill = (it) => {
    let tempData = [...dataFromStorage];
    tempData.splice(it, 1);
    setDataFromStorage([...tempData]);

   const new1 = tempData.filter((item) => item.name !== it);
    localStorage.setItem("dataList", JSON.stringify(new1));
  };

  return (
    <>
      <style>
        {`
      table {
  font-family: arial, sans-serif;
  border-collapse: collapse;
  width: 100%;
}

td, th {
  border: 1px solid #dddddd;
  text-align: left;
  padding: 8px;
}
      `}
      </style>
      <Link to="/add">
        <Button className="btn btn-info center m-lg-5" onClick={props.AddNewBill}>
          Add New Bill
        </Button>
        </Link>
      <InputGroup className="mb-3">
        {console.log(dataFromStorage)}
        <InputGroup.Text>Name:</InputGroup.Text>
        <Form.Control
          aria-label="Name:"
          placeholder="Client's Name"
          value={cusname}
          onChange={(e) => setCusName(e.target.value)}
          required
        />
        <button
          type="button"
          disabled={!cusname}
          className="btn btn-success"
          onClick={getDataFromLocalStorage}
        >
          Search
        </button>
      </InputGroup>
      <table>
        <thead>
          <tr>
            <th>Bill Name</th>
            <th>Bill Date</th>
            <th>Item</th>
            <th>Quantity</th>
            <th>Price</th>
            <th>GST</th>
            <th>Sub-total</th>
            <th>Total</th>
          </tr>
        </thead>
        <tbody>
          {dataFromStorage.map((data, i) => (
            <tr key={i}>
              <td>{data.name}</td>
              <td>{data.date}</td>
              <td>{data.item}</td>
              <td>{data.quantity}</td>
              <td>{data.price}</td>
              <td>{data.gst}</td>
              <td>{data.subtotal}</td>
              <td>{data.total}</td>
              <Button className="btn btn-danger" onClick={() => {
                      deleteBill(data.i);
                    }}>Delete</Button>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

export default PreBill;
